//import Layout from "../component/layout";
import React, { useState } from "react";
import ReactModal from "react-modal";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import "bootstrap/dist/css/bootstrap.min.css";

const ModalTambahTamu = () => {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <Button variant="primary" onClick={handleOpenModal}>
        Launch demo modal
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formNamaPerusahaan">
              <Form.Label>Nama Perusahaan</Form.Label>
              <Form.Control type="name" placeholder="Masukan Nama Perusahaan" />
            </Form.Group>

            <Form.Group controlId="form">
              <Form.Label>Nama Pemesan</Form.Label>
              <Form.Control type="password" placeholder="Masukan Nama Pemesan" />
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
              <Form.Label>Nama Pemesan</Form.Label>
              <Form.Control type="password" placeholder="Masukan Nama Pemesan" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
export default ModalTambahTamu;
