//import Layout from "../component/layout";
import React, { useState } from "react";
import ReactModal from "react-modal";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import DatePicker from "react-bootstrap-date-picker";
import "bootstrap/dist/css/bootstrap.min.css";

const ModalTambahTamu = () => {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleChange = (value, formattedValue) => {
    this.setState({
      value: value, // ISO String, ex: "2016-11-19T12:00:00.000Z"
      formattedValue: formattedValue, // Formatted String, ex: "11/19/2016"
    });
  };

  return (
    <>
      <Button variant="primary" onClick={handleOpenModal}>
        Launch demo modal
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formNamaPerusahaan">
              <Form.Label>Nama Perusahaan</Form.Label>
              <Form.Control type="name" placeholder="Masukan Nama Perusahaan" />
            </Form.Group>

            <Form.Group controlId="formNamaPemesan">
              <Form.Label>Nama Pemesan</Form.Label>
              <Form.Control type="name" placeholder="Masukan Nama Pemesan" />
            </Form.Group>
            <Form.Group controlId="Tanggalpemakaian">
              <Form.Label>Tanggal Pemakaian</Form.Label>
              <DatePicker
                id="example-datepicker"
                value={this.state.value}
                onChange={this.handleChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
export default ModalTambahTamu;
